/* Function: mdlInitializeSizes ===============================================
 * Abstract:
 *   Setup sizes of the various vectors.
 */
static void mdlInitializeSizes(SimStruct *S)
{
    //int_T m_net=0;
    //IMPORTANT: It is necessary get a real and then cat it to int
    const real_T *m_txbufsize = mxGetData(PARAM_DEF3(S));
    int m_txbufsize_int = (int)*m_txbufsize;

    const real_T *m_rxbufsize = mxGetData(PARAM_DEF4(S));
    int m_rxbufsize_int = (int)*m_rxbufsize;

    const real_T *m_txtout    = mxGetData(PARAM_DEF5(S));
    int m_txtout_int = (int)*m_txtout;

    const real_T *m_rxtout    = mxGetData(PARAM_DEF6(S));
    int m_rxtout_int = (int)*m_rxtout;

    unsigned long m_mode      =0;
    int m_baudrate;
    int m_net;
    int_T i,j=0;
    const int16_T  *baudrate  = mxGetData(PARAM_DEF0(S)); 
    
    DECL_AND_INIT_DIMSINFO(inputDimsInfo);
    DECL_AND_INIT_DIMSINFO(outputDimsInfo);
    ssSetNumSFcnParams(S, NPARAMS);  /* Number of expected parameters */
      #if defined(MATLAB_MEX_FILE)
	if (ssGetNumSFcnParams(S) == ssGetSFcnParamsCount(S)) {
	  mdlCheckParameters(S);
	  if (ssGetErrorStatus(S) != NULL) {
	    return;
	  }
	 } else {
	   return; /* Parameter mismatch will be reported by Simulink */
	 }
      #endif
    
    ssSetNumContStates(S, NUM_CONT_STATES);
    ssSetNumDiscStates(S, NUM_DISC_STATES);

    if (!ssSetNumInputPorts(S, NUM_INPUTS)) return;
    inputDimsInfo.width = INPUT_0_WIDTH;
    ssSetInputPortDimensionInfo(S, 0, &inputDimsInfo);
    ssSetInputPortFrameData(S, 0, IN_0_FRAME_BASED);
    ssSetInputPortDataType(S, 0, SS_DOUBLE);
    ssSetInputPortComplexSignal(S, 0, INPUT_0_COMPLEX);
    ssSetInputPortDirectFeedThrough(S, 0, INPUT_0_FEEDTHROUGH);
    ssSetInputPortRequiredContiguous(S, 0, 1); /*direct input signal access*/
    
    inputDimsInfo.width = INPUT_1_WIDTH;
    ssSetInputPortDimensionInfo(S, 1, &inputDimsInfo);
    ssSetInputPortFrameData(S, 1, IN_1_FRAME_BASED);
    ssSetInputPortDataType(S, 1, SS_DOUBLE);
    ssSetInputPortComplexSignal(S, 1, INPUT_1_COMPLEX);
    ssSetInputPortDirectFeedThrough(S, 1, INPUT_1_FEEDTHROUGH);
    ssSetInputPortRequiredContiguous(S, 1, 1); /*direct input signal access*/

    //toggle button
    inputDimsInfo.width = INPUT_1_WIDTH; 
    ssSetInputPortDimensionInfo(S, 2, &inputDimsInfo);
    ssSetInputPortFrameData(S, 2, FRAME_NO);
    ssSetInputPortDataType(S, 2, SS_INT8);
    ssSetInputPortComplexSignal(S, 2, COMPLEX_NO);
    ssSetInputPortDirectFeedThrough(S, 2, 1);
    ssSetInputPortRequiredContiguous(S, 2, 1); /*direct input signal access*/

    //send message button
    inputDimsInfo.width = INPUT_1_WIDTH;
    ssSetInputPortDimensionInfo(S, 3, &inputDimsInfo);
    ssSetInputPortFrameData(S, 3, FRAME_NO);
    ssSetInputPortDataType(S, 3, SS_INT8);
    ssSetInputPortComplexSignal(S, 3, COMPLEX_NO);
    ssSetInputPortDirectFeedThrough(S, 3, 1);
    ssSetInputPortRequiredContiguous(S, 3, 1); /*direct input signal access*/




    if (!ssSetNumOutputPorts(S, NUM_OUTPUTS)) return;
    outputDimsInfo.width = OUTPUT_0_WIDTH;
    ssSetOutputPortDimensionInfo(S, 0, &outputDimsInfo);
    ssSetOutputPortFrameData(S, 0, OUT_0_FRAME_BASED);
    ssSetOutputPortDataType(S, 0, SS_DOUBLE);
    ssSetOutputPortComplexSignal(S, 0, OUTPUT_0_COMPLEX);
    ssSetNumSampleTimes(S, 1);
    ssSetNumRWork(S, 0);
    ssSetNumIWork(S, 0);
    ssSetNumPWork(S, 0);
    ssSetNumModes(S, 0);
    ssSetNumNonsampledZCs(S, 0);

    /* Take care when specifying exception free code - see sfuntmpl_doc.c */
    ssSetOptions(S, (SS_OPTION_EXCEPTION_FREE_CODE |
                     SS_OPTION_USE_TLC_WITH_ACCELERATOR |
		     SS_OPTION_WORKS_WITH_CODE_REUSE));
    const real_T  *netid = mxGetData(PARAM_DEF1(S));
    m_net=(int) *netid;
    #ifdef ESDCAN_IS_CONNECTED
    ret = canOpen(m_net, m_mode, m_txbufsize_int, m_rxbufsize_int, m_txtout_int, m_rxtout_int, &m_h0);
    #else
    printf("CanOpen(mnet=%d, m_mode=%d, m_txbufsize=%d, m_rxbufsize=%d, m_txtout=%d, m_rxtout=%d)", m_net, m_mode, m_txbufsize_int, m_rxbufsize_int, m_txtout_int, m_rxtout_int, m_net);
    ret=NTCAN_SUCCESS;
    #endif
  if(ret == NTCAN_SUCCESS)
    {
     x=1;
    }
   else
   {
	  x=-1;
      
     ssSetErrorStatus(S,"CanOpen failed");

      return;
   }
  m_baudrate=(int) *baudrate;
  #ifdef ESDCAN_IS_CONNECTED
  ret = canSetBaudrate(m_h0,m_baudrate);//canSetBaudrate(m_h0, z-1);
    #else
    printf("canSetBaudrate(m_baudrate=%d)", m_baudrate);
    ret=NTCAN_SUCCESS;
    #endif
   if(ret == NTCAN_SUCCESS)
    {
     x=1;
    }
   else
   {
	  x=-1;
      //ssSetErrorStatus(S,"CANSetBaudrate failed");
      return;
   }
  
  for (i =0; i<2047; i++)
  {

      #ifdef ESDCAN_IS_CONNECTED
        ret = canIdAdd(m_h0,i);
        #else
        //printf("canIdAdd(id=%d)", i);
        ret=NTCAN_SUCCESS;
        #endif
  if(ret == NTCAN_SUCCESS)
    {
     x=1;
    }
   else
   {
	  x=-1;
     //ssSetErrorStatus(S,"CANIdAdd failed");
      return;   
   }
  }


  //get memory in order to save the value of "send message" button
  ssSetNumIWork(S, 1); 
}

