/* Function: mdlOutputs =======================================================
 *
*/
static void mdlOutputs(SimStruct *S, int_T tid)
{
    const real_T   *u  = (const real_T*) ssGetInputPortSignal(S,0);
    real_T        *status  = (real_T *)ssGetOutputPortRealSignal(S,0);
    const int_T   p_width0  = mxGetNumberOfElements(PARAM_DEF0(S));
    const int_T   p_width1  = mxGetNumberOfElements(PARAM_DEF1(S));
    const int_T   p_width2  = mxGetNumberOfElements(PARAM_DEF2(S));
    const real_T  *BAUDRATE  = mxGetData(PARAM_DEF0(S));
    //const real_T  *ID  = mxGetData(PARAM_DEF1(S));
    const real_T *ID = (const real_T*) ssGetInputPortSignal(S,1);
    const real_T  *LENGHT  = mxGetData(PARAM_DEF2(S));
    int i=0;
    long len=1;
    int8_T *switch_button = (int8_T*)ssGetInputPortSignal(S,2);

    //read 'send message' button
    int8_T* button;
    int8_T *lastbutton= ( int8_T *)ssGetIWork(S);
    button = (int8_T*)ssGetInputPortSignal(S,3);

    //prepare the message
    tx_message.id=(int)*ID;
    tx_message.len=(unsigned char)*LENGHT;
    for (i=0;i<8;i++)
    {
        tx_message.data[i]=(int) u[i];
    }

    //if (stream enabled or (the 'send message' button has been pressed))
    if( ( *switch_button == 1) || ((*lastbutton==0) && (*button==1)) )
    {
        #ifdef ESDCAN_IS_CONNECTED
        ret=canWrite(m_h0, &tx_message, &len,NULL);  
        #else
        printf("Send message with ID=%d payload=[%d %d %d %d %d %d %d ]", tx_message.id, tx_message.data[0],tx_message.data[1],tx_message.data[2], tx_message.data[3], tx_message.data[4], tx_message.data[5], tx_message.data[6], tx_message.data[7]  );
        #endif
    }

   *lastbutton = *button;
   

  status[0]=1;
  
}



